function words(a,b){var c=a.split(""),d=document.getElementById("term");if(a===b)d.innerHTML+="<br/>ERROR: The strings must be different<br/>";else if(a.length===b.length){d.innerHTML+="<br/>"+c.join("")+"<br/>";for(var e=0;e<a.length;e++)c[e]=b[e],d.innerHTML+=c.join("")+"<br/>";d.innerHTML+="  "}else d.innerHTML+="<br/>ERROR: The strings must be of equal length<br/>"}function custom(){var a=document.forms[0][0].value,b=document.forms[0][1].value;words(a,b)}